import { Request, Response } from 'express';

import AddressService from '../services/address.service';

export default class AddressController {
  private addressService: AddressService;

  constructor() {
    this.addressService = new AddressService();
  }

  public getAll = async (req: Request, res: Response) => {
    const response = await this.addressService.getAll();

    return res.status(response.type).json(response.message);
  };

  public getById = async (req: Request, res: Response) => {
    const id = parseInt(req.params.id);
    const response = await this.addressService.getById(id);

    return res.status(response.type).json(response.message);
  };

  public post = async (req: Request, res: Response) => {
    const address = req.body;
    const response = await this.addressService.post(address);

    return res.status(response.type).json(response.message);
  };

  public put = async (req: Request, res: Response) => {
    const id = parseInt(req.params.id);
    const address = req.body;
    const response = await this.addressService.put(id, address);

    return res.status(response.type).json(response.message);
  };

  public delete = async (req: Request, res: Response) => {
    const id = parseInt(req.params.id);
    const response = await this.addressService.delete(id);

    return res.status(response.type).json(response.message);
  };

  public patch = async (req: Request, res: Response) => {
    const id = parseInt(req.params.id);
    const address = req.body;
    const response = await this.addressService.patch(id, address);

    return res.status(response.type).json(response.message);
  };
}

import Address from '../models/Address';

export default class AddressService {
  public getAll = async () => {
    try {
      const allAddresses = await Address.findAll();
      return { type: 200, message: allAddresses };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public getById = async (id: number) => {
    try {
      const address = await Address.findByPk(id);

      if (!address) {
        return { type: 404, message: 'Address not found.' };
      }
      return { type: 200, message: address };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public post = async (address: Address) => {
    try {
      const newAddress = await Address.create(address);
      return { type: 201, message: newAddress };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public put = async (id: number, address: Address) => {
    try {
      const addressToUpdate = await Address.findByPk(id);

      if (!addressToUpdate) {
        return { type: 404, message: 'Address not found.' };
      }

      await addressToUpdate.update(address);
      return { type: 200, message: addressToUpdate };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public delete = async (id: number) => {
    try {
      const address = await Address.findByPk(id);

      if (!address) {
        return { type: 404, message: 'Address not found.' };
      }

      await address.destroy();
      return { type: 200, message: 'Address deleted.' };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public patch = async (id: number, updates: Partial<Address>) => {
    try {
      const address = await Address.findByPk(id);

      if (!address) {
        return { type: 404, message: 'Address not found.' };
      }

      await address.update(updates);
      return { type: 200, message: address };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

};

