export default class CustomerService {
  public getAll = async () => {
    try {
      const allCustomers = await Customer.findAll();
      return { type: 200, message: allCustomers };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  getById = async (id: number) => {
    try {
      const customer = await Customer.findByPk(id, { include: [{ all: true, nested: true }] });

      if (!customer) {
        return { type: 404, message: "Customer not found." };
      }
      return { type: 200, message: customer };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public getByCpf = async (cpf: string) => {
    try {
      const customer = await Customer.findOne({ where: { cpf } });
      if (!customer) {
        return { type: 404, message: "Customer not found." };
      }

      return { type: 200, message: customer };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public getByEmail = async (email: string) => {
    try {
      const customer = await Customer.findOne({ where: { email } });

      if (!customer) {
        return { type: 404, message: "Customer not found." };
      }

      return { type: 200, message: customer };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public post = async (customer: Customer, address?: Address) => {
    try {
      const newCustomer = await Customer.create(customer);

      if (!address) {
        return { type: 201, message: newCustomer };
      } else {
        const newAddress = await Address.create(address);

        newCustomer.address_id = newAddress.id;
        await newCustomer.save();

        const customerCreated = await Customer.findByPk(newCustomer.id, { include: [{ model: Address, as: 'address'}] });

        return { type: 201, message: customerCreated };
      }
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public put = async (id: number, customer: Customer) => {
    try {
      const customerToUpdate = await Customer.findByPk(id);

      if (!customerToUpdate) {
        return { type: 404, message: "Customer not found." };
      }

      await customerToUpdate.update(customer);
      return { type: 200, message: customerToUpdate };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };

  public delete = async (id: number) => {
    try {
      const customer = await Customer.findByPk(id);

      if (!customer) {
        return { type: 404, message: "Customer not found." };
      }

      await customer.destroy();
      return { type: 200, message: "Customer deleted." };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  }

  public patch = async (id: number, updates: Partial<Customer>) => {
    try {
      const customer = await Customer.findByPk(id);

      if (!customer) {
        return { type: 404, message: "Customer not found." };
      }

      await customer.update(updates);
      return { type: 200, message: customer };
    } catch (err) {
      return { type: 500, message: (err as Error).message };
    }
  };
};

import {Model, DataTypes, InferAttributes, InferCreationAttributes } from 'sequelize';
import db from '.';

import Workoutsheet from './Workoutsheet';
import Address from './Address';

export default class Customer extends Model<InferAttributes<Customer>, InferCreationAttributes<Customer>> {
  declare id?: number;
  declare first_name: string;
  declare last_name: string;
  declare email: string;
  declare gender: string;
  declare phone: string;
  declare cpf: string;
  declare isActive: boolean;

  public address_id?: number;
}

Customer.init({
  id: {
    type: DataTypes.INTEGER,
    primaryKey: true,
    autoIncrement: true,
    allowNull: false,
  },
  first_name: {
    type: DataTypes.STRING,
    allowNull: false,
  },
  last_name: {
    type: DataTypes.STRING,
    allowNull: false,
  },
  email: {
    type: DataTypes.STRING,
    allowNull: false,
    unique: true,
  },
  gender: {
    type: DataTypes.STRING,
    allowNull: false,
  },
  phone: {
    type: DataTypes.STRING,
    allowNull: false,
    unique: true,
  },
  cpf: {
    type: DataTypes.STRING,
    allowNull: false,
    unique: true,
  },
  isActive: {
    type: DataTypes.BOOLEAN,
    allowNull: false,
    defaultValue: true,
  },
  address_id: {
    type: DataTypes.INTEGER,
    allowNull: true,
    references: {
      model: 'address',
      key: 'id'
    }
  },
}, {
  sequelize: db,
  modelName: 'customer',
  tableName: 'customer',
  underscored: true,
  timestamps: false,
});

Customer.hasMany(Workoutsheet, {
  foreignKey: 'customer_id',
  as: 'workoutsheets',
  constraints: false,
});

Workoutsheet.belongsTo(Customer, {
  foreignKey: 'customer_id',
  as: 'customer',
  constraints: false,
});

Customer.hasOne(Address, {
  foreignKey: 'id',
  as: 'address',
  constraints: false,
});

